import React from 'react';
import styled from 'styled-components';

import FormGroup from '../../../common/Forms/FormGroup.jsx';

const formFields = {
    nameField: {
        id: 'userName',
        label: 'Name',
        inputType: 'text',
        value: '',
    },
    emailField: {
        id: 'userEmail',
        label: 'Email',
        inputType: 'email',
        value: '',
    },
    messageField: {
        id: 'userMessage',
        label: 'Message',
        inputType: 'textarea',
        value: '',
    },
}
const ContactForm = () => {

    const handleOnChange = (event, formField) => {
        console.log(event.target.value, formField);
    }

    return (
        <ContactFormStyled className='ContactForm'>
            <form>
                <FormGroup formField={formFields.nameField}     onChange={ handleOnChange }   />
                <FormGroup formField={formFields.emailField}    onChange={ handleOnChange }   />
                <FormGroup formField={formFields.messageField}  onChange={ handleOnChange }   />
            </form>
        </ContactFormStyled>
    );
}

export default ContactForm;

const ContactFormStyled = styled.div`

`;
